function somar(t,e){return Number(t)+Number(e)}function subtrair(t,e){return t-e}function multiplicar(t,e){return t*e}function dividir(t,e){return t/e}const buttonSoma=document.querySelector("#button-somar"),buttonSubtrai=document.querySelector("#button-subtrair"),buttonMultiplica=document.querySelector("#button-multiplicar"),buttonDivide=document.querySelector("#button-dividir");let divResultado=document.querySelector("#resultado");buttonSoma.addEventListener("click",()=>{const t=document.querySelector("#numero1").value,e=document.querySelector("#numero2").value;divResultado.textContent=`A soma de ${t} + ${e} = ${somar(t,e)}`}),buttonSubtrai.addEventListener("click",()=>{const t=document.querySelector("#numero1").value,e=document.querySelector("#numero2").value;subtrair(t,e),divResultado.textContent=`A subtração de ${t} - ${e} = ${subtrair(t,e)}`}),buttonMultiplica.addEventListener("click",()=>{const t=document.querySelector("#numero1").value,e=document.querySelector("#numero2").value;multiplicar(t,e),divResultado.textContent=`A multiplicação de ${t} * ${e} = ${multiplicar(t,e)}`}),buttonDivide.addEventListener("click",()=>{const t=document.querySelector("#numero1").value,e=document.querySelector("#numero2").value;dividir(t,e),divResultado.textContent=`A divisão de ${t} / ${e} = ${dividir(t,e)}`});